    @Test
    public void testGetDataFromMap(){
        assertEquals(APITMDB.getMovieId(movieMap), 569094);
        assertEquals(APITMDB.getMovieOriginalTitle(movieMap), "Spider-Man: Across the Spider-Verse");
        assertEquals(APITMDB.getMovieOverview(movieMap), "After reuniting with Gwen Stacy, Brooklynâ€™s full-time, friendly neighborhood Spider-Man is catapulted across the Multiverse, where he encounters the Spider Society, a team of Spider-People charged with protecting the Multiverseâ€™s very existence. But when the heroes clash on how to handle a new threat, Miles finds himself pitted against the other Spiders and must set out on his own to save those he loves most.");
        assertEquals(APITMDB.getMovieBannerURL(movieMap), "https://image.tmdb.org/t/p/original/2I5eBh98Q4aPq8WdQrHdTC8ARhY.jpg");
        assertEquals(APITMDB.getMoviePosterURL(movieMap), "https://image.tmdb.org/t/p/original/8Vt6mWEReuy4Of61Lnj5Xj704m8.jpg");
        assertEquals(APITMDB.getMovieReleaseDate(movieMap), "2023-05-31");
        for (Genre genre: APITMDB.getMovieGenres(movieMap)) {
            assertEquals(genre.getName(), "genre1");
        }
        assertEquals(APITMDB.getNumberOfMoviesForAGenre(detailsMap), 36588);
        assertEquals(APITMDB.getNumberOfPages(detailsMap), 1830);
    }

    @Test
    public void testGetGenresFromAStringJson() {
        String stringJson = "[{id=28, name=Action}, {id=12, name=Adventure}, {id=16, name=Animation}, {id=878, name=Fiction}]";
        List<Genre> genres = APITMDB.getGenresFromAStringJson(stringJson);
        Genre genre0 = new Genre(28, "Action");
        Genre genre1 = new Genre(12, "Adventure");
        Genre genre2 = new Genre(16, "Animation");
        Genre genre3 = new Genre(878, "Fiction");
        assertEquals(genre0, genres.get(0));
        assertEquals(genre1, genres.get(1));
        assertEquals(genre2, genres.get(2));
        assertEquals(genre3, genres.get(3));
    }

